import React, { useState, useEffect } from 'react'
import { db } from '../../base';
import { collection, addDoc, deleteDoc } from "firebase/firestore"; 

function UpdateContactForm() {

  const [contacts, setContacts] = useState([{}])
  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('');

  const contactsCollection = db.collection("contacts");
  useEffect(()=>{
    contactsCollection.onSnapshot((snapshot)=>{
      const contactsData = [];
      snapshot.docs.map((doc)=>{
        contactsData.push({
          id: doc.id,
          Name: doc.data().Name,
          Email: doc.data().Email,
          Role: doc.data().Role
        })
      })
      setContacts(contactsData)
    })
  })

  function createItemInList(contact) {
    return(<li><label><input type="checkbox" onClick={deleteItemInList}></input>{contact.Name}</label></li>)
  }

  function deleteItemInList(contact) {
    // needs to search the database for contact.Name's contact.Id
    console.log(contact.id)
    console.log(contacts)


  }
  return (
    <div>
      <div className="addContactDiv">
        <h1>Add Contact </h1>
        <form>
          <label>
            Name: <input type='text' value={fullname} onChange={(e)=>{setFullname(e.target.value)}}/>
          </label>
          <label>
            Email: <input type='text' value={email} onChange={(e)=>setEmail(e.target.value)}/>
          </label>
          <label>
            Role: <input type='text' value={role} onChange={(e)=>setRole(e.target.value)}/>
          </label>
          <input type="submit" value="Submit" onClick={(e) => {
              e.preventDefault();
              console.log('here')
              addDoc(collection(db, "contacts"), {
                Name: fullname,
                Email: email,
                Role: role
              });
            }} />
        </form>


    </div>

    <div className="deleteContactDiv">
      <h1>Delete Contact(s)</h1>
      <ul style={{ listStyle: "none" }}>
        {contacts.map(createItemInList)}
      </ul>

    </div>
    </div>

  )
}

export default UpdateContactForm
