    font-family: 'Montserrat', sans-serif;
    background: #A3BDA7;
    padding-bottom: 25px;
    padding-top:25px;
`

export const MPTitle = styled.h1`
    margin-right:20px;
    font-size:2.5em;
`

export const IBTitle = styled.h1`
    margin-left:20px;
    font-size:2.5em;
`

export const Logo = styled.img`
    padding:5px;
`
export const MissionStatement = styled.p`
    font-family: 'Montserrat', sans-serif;
