import React, { useState, useEffect } from 'react'
import { db } from '../../base';
import { doc, collection, addDoc, deleteDoc } from "firebase/firestore"; 
import {AddContact, DeleteContact, Body} from './styles'
function UpdateContactForm() {

  const [contacts, setContacts] = useState([{}])
  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [role, setRole] = useState('');
  const [checkedState, setCheckedState] = useState(new Array(contacts.length).fill(false))

  const contactsCollection = db.collection("contacts");
  useEffect(()=>{
    contactsCollection.onSnapshot((snapshot)=>{
      const contactsData = [];
      snapshot.docs.map((doc)=>{
        contactsData.push({
          id: doc.id,
          Name: doc.data().Name,
          Email: doc.data().Email,
          Role: doc.data().Role
        })
      })
      setContacts(contactsData)
    })
  }, [])


  const handleOnChange = (idx) => {

    const updatedCheckedState = contacts; 
    updatedCheckedState[idx]=true;

    setCheckedState(updatedCheckedState);

  }

  const handleOnSubmit = (e) => {
    e.preventDefault();
    // iterate through the checkedState array and looked for the checked indices and then get the id of those items and then deleteDoc
    checkedState.forEach((state,index)=>{
      console.log(state)
      if(state==true){
        const contactIDToBeDeleted = contacts[index].id;
        deleteDoc(doc(db,"contacts",contactIDToBeDeleted));

      }
    })
  }

  return (
    <Body>
      <div className="addContactDiv">
        <AddContact>Add Contact </AddContact>
        <form>
          <label>
            Name: <input type='text' value={fullname} onChange={(e)=>{setFullname(e.target.value)}}/>
          </label>
          <label>
            Email: <input type='text' value={email} onChange={(e)=>setEmail(e.target.value)}/>
          </label>
          <label>
            Role: <input type='text' value={role} onChange={(e)=>setRole(e.target.value)}/>
          </label>
          <input type="submit" value="Submit" onClick={(e) => {
              e.preventDefault();
              addDoc(collection(db, "contacts"), {
                Name: fullname,
                Email: email,
                Role: role
              });

            }} />
        </form>


    </div>

    <div className="deleteContactDiv">
      <DeleteContact>Delete Contact(s)</DeleteContact>
      <form>
      <ul style={{'listStyle':'none'}}>
      {contacts.map((item, index)=>{

          return(
            <li key={index}>

              <label><input type="checkbox" checked={checkedState[index]} name={item.Name} value={item.Name} onChange={()=>handleOnChange(index)}/>{item.Name}</label>
            </li>

          )
        })}
      </ul>
      <input type="submit" value="Submit" onClick={(e)=>handleOnSubmit(e)}/>

      </form>



    </div>
    </Body>

  )
}

export default UpdateContactForm
