import styled from 'styled-components'

export const Background = styled.div`
    background: #d3dbd3;
    border-radius:20px;
    margin:20px 200px;
    padding:10px;

    @media only screen and (max-width: 840px) {
        margin:20px 100px;
    }
    @media only screen and (max-width: 640px) {
        margin:20px 3rem;
    }
`
