import React, {useState, useEffect} from 'react'
import { collection, addDoc, deleteDoc, doc } from "firebase/firestore"; 
import { Body, Heading, Centering, Background, SubmitButton, FormElements} from './styles'
import { storage, db } from '../../base'

const UpdatePhotosForm = () => {
    const [imageUpload, setImageUpload] = useState(null)
    const [photoURL, setPhotoURL] = useState('')

    const [imgsToDelete, setImgs] = useState([])
    const [imgList, setImgList] = useState({})
    const [imgIds, setImgIds] = useState([])

    //const imgRef = storage.ref().child(`/images/${}`)
    const imgURLCollection = db.collection("photos");

    useEffect(()=>{
        const data = []
        imgURLCollection.onSnapshot((snapshot)=>{
            snapshot.docs.map((doc)=>{
                data.push({
                    id:doc.id,
                    url:doc.data().url
                })
            })
            setImgList(data);
        })
    },[])

    const deleteImgs = (e) => {
        e.preventDefault();
        imgList.forEach((el)=>{
            if(document.getElementById(el.url).checked==true){
              console.log(el.url);
              deleteDoc(doc(db,"photos",el.id));

              // basically now you need to string parse the name out of the url and have that display instead and also access firebase storage and delete the image with that name (need to delete url firestore and the img itself in storage)
            }

          })

    }

    const uploadURL = (url) => {  
        console.log(url)
        addDoc(collection(db,'photos'),{
            url: url
        })

    }
    const uploadImage = (e) => {
        e.preventDefault()
        const fileType = imageUpload['type'];
        console.log(fileType)
        if(imageUpload==null) return;
        console.log(imageUpload)
        const storageRef = storage.ref()
        const uploadTask = storageRef.child(`/images/${imageUpload.name}`).put(imageUpload)
        uploadTask.on('state_changed', 
            (snapshot)=>{
                console.log(snapshot)  

            }, 
            (err)=>{
                console.log(err)
            }, 
            ()=>{
            storage
                .ref("/images")
                .child(imageUpload.name)
                .getDownloadURL()
                .then((url)=>{
                    console.log(`File available at: ${url}`)
                    // need to send url to firestore photos collection
                    setPhotoURL(url);
                    // setPhotoURLs(...photoURLs, url)
                    uploadURL(url);
                    alert("success")
                });

        })
    }
  return (
    <Body>

        <Centering>
            <Background>
                <Heading>Add Images</Heading>
                <FormElements>

                    <input type='file' onChange={(e)=>{setImageUpload(e.target.files[0])}}></input>
                    <SubmitButton onClick={(e)=>uploadImage(e)}>Upload Image</SubmitButton>

                </FormElements>

            </Background>


            <Background>
                <Heading>
                    Delete Images
                </Heading>
                 <div>
                    <ul style={{'listStyle':'none'}}>
                    {imgList.map((item, index)=>{

                        return(
                            <li key={index}>

                            <label><input id={item.url} type="checkbox" value={item.url}/>{item.url}</label>
                            </li>

                        )
                        })}
                    </ul>
                    <SubmitButton type="submit" value="Submit" onClick={(e)=>deleteImgs(e)}>Submit</SubmitButton>

                    </div>
            </Background>
        </Centering>
    </Body>
  )
}

export default UpdatePhotosForm
