import React, { useState, useContext, useEffect } from "react";
import { auth, db } from "../../base";
import { signOut } from "firebase/auth";
import { AuthContext } from "../../AuthProvider";
import { useNavigate, Link } from "react-router-dom";
import { ReactEmbeddedGoogleCalendar } from 'react-embedded-google-calendar';
import PageHeader from "../../components/PageHeader/PageHeader";
import AnnouncementList from "../../components/AnnouncementsList/AnnouncementList";
import Navbar from "../../components/Navbar/Navbar";
import { AdminLoginButton, AdminLoginButtonContainer, Container, AnnouncementsContainer } from './styles';


function Home() {
  const { currentUser } = useContext(AuthContext);
  const navigate = useNavigate();


  const clickLogin = () => {
    if (currentUser) {
      signOut(auth);
    } else {
      navigate("/login");
    }
  };

  const clickSignup = () => {
    navigate("/signup");
  };

  return (
    <div className="mainContainer">


      <PageHeader />
        <Navbar />
        {currentUser && console.log("authenticated")}
      <AdminLoginButtonContainer>
        <AdminLoginButton onClick={clickLogin}>
          {currentUser ? "Log Out" : "Log In"}
        </AdminLoginButton>
      </AdminLoginButtonContainer>
      <Container>
        <AnnouncementsContainer>
          <AnnouncementList />
          </AnnouncementsContainer>
        <div className="calendar">
        <ReactEmbeddedGoogleCalendar publicUrl="https://calendar.google.com/calendar/embed?src=myersparkibwebsite%40gmail.com&ctz=America%2FNew_York&title=Myers%20Park%20IB%20Calendar" width='400px' height='400px'></ReactEmbeddedGoogleCalendar>
      </div>
      </Container>
      {currentUser && <Link to='/admin'><button>Add Announcement</button></Link>} 

    </div>
  );
}

export default Home;
