import React, {useState} from 'react'
import { Body, Heading, Centering, Background, SubmitButton, FormElements} from './styles'
import { storage } from '../../base'
import  { v4 } from 'uuid'

const UpdatePhotosForm = () => {
    const [imageUpload, setImageUpload] = useState(null)
    const [progress, setProgress] = useState(0)

    const uploadImage = () => {
        if(imageUpload==null) return;
        console.log(imageUpload)
        const uploadTask = storage.ref(`images/${imageUpload.name}`).put(imageUpload)
        uploadTask.on('state_changed', 
            (snapshot)=>{
                console.log(snapshot)
            }, 
            (err)=>{
                console.log(err)
            }, 
            ()=>{
            storage
                .ref("images")
                .child(imageUpload.name)
                .getDownloadURL()
                .then((url)=>{console.log(url)})
        })
    }
  return (
    <Body>

        <Centering>
            <Background>
                <Heading>Add Images</Heading>
                <FormElements>

                    <input type='file' onChange={(e)=>{setImageUpload(e.target.files[0])}}></input>
                    <SubmitButton onClick={uploadImage}>Upload Image</SubmitButton>
                </FormElements>

            </Background>
        </Centering>
    </Body>
  )
}

export default UpdatePhotosForm
