import { db } from '../../base';
import { collection, addDoc, deleteDoc, doc } from "firebase/firestore"; 
import { Heading, Body, FormElements, SubmitButton, Background, Centering } from './styles'

const UpdateLinksForm = () => {
  const [links, setLinks] = useState([{}]);
  const [newURL, setNewURL] = useState('')
  const [newLinkedTo, setNewLinkedTo] = useState('')
  return (
    <div>
      <Centering>
        <Background>
          <Heading>Add Link</Heading>
          <FormElements>
            <label>
              Link Name: <input type='text' onChange={(e)=>setNewLinkedTo(e.target.value)}></input>
            </label>
            <label>
              URL: <input type='text' onChange={(e)=>setNewURL(e.target.value)}/>
            </label>
          </FormElements>
        </Background>
      </Centering>
    </div>
  )
}

export default UpdateLinksForm
