import React, {useState} from 'react'
import { db } from '../../base';
import { collection, addDoc } from "firebase/firestore"; 
import { Heading, Body, FormElements, SubmitButton, Background } from './styles'

function UpdateAnnouncementForm() {
    const [title, setTitle] = useState('')
    const [details, setDetails] = useState('')


  return (
    <Body>
      <Background>
      <Heading>Add Announcement</Heading>
        <FormElements>

            <label>
                Title: <input type='text' value={title} onChange={(e)=>setTitle(e.target.value)}/>
            </label>
            <label>
                Details: <input type='text' value={details} onChange={(e)=>setDetails(e.target.value)}/>
            </label>
            <SubmitButton type='submit' value="Submit" onClick={(e)=>{
                e.preventDefault();
               if((title!='')&&(details!='')){
                addDoc(collection(db, 'announcements'), {
                    title: title, 
                    details: details
                })
               }
            }}>Submit</SubmitButton>

          </FormElements>
      </Background>



    </Body>
  )
}

export default UpdateAnnouncementForm
