import React, {useState} from 'react'
import { db } from '../../base';
import { collection, addDoc } from "firebase/firestore"; 
import { AddAnnouncement, Body, FormElements } from './styles'

function UpdateAnnouncementForm() {
    const [title, setTitle] = useState('')
    const [details, setDetails] = useState('')


  return (
    <Body>
        <AddAnnouncement>Add Announcement</AddAnnouncement>
        <FormElements>
        <form>
            <label>
                Title: <input type='text' value={title} onChange={(e)=>setTitle(e.target.value)}/>
            </label>
            <label>
                Details: <input type='text' value={details} onChange={(e)=>setDetails(e.target.value)}/>
            </label>
            <input type='submit' value="Submit" onClick={(e)=>{
                e.preventDefault();
                addDoc(collection(db, 'announcements'), {
                    title: title, 
                    details: details
                })
            }}/>
        </form>
          </FormElements>


    </Body>
  )
}

export default UpdateAnnouncementForm
