import React, {useState, useRef, useEffect} from 'react'
import Feed from './Feed'
import axios from 'axios'


const InstaFeed =({token, ...props}) => {
    const [feed, setFeed] = useState([]);
    const tokenProp = useRef(token);
    tokenProp.current = token;


    useEffect(()=>{
        axios.get(`https://graph.instagram.com/me/media?fields=id,media_type,media_url,caption&limit=${props.limit}&access_token=${props.token}`).then((response)=>{setFeed(response.data.data)}).catch((err)=>console.log(err));
    }, [props.limit])
  return (
    <div>
        {feed.map((f)=>{
            <Feed key={f.id} igfeed={f}/>
        })}
    </div>
  )
}

export default InstaFeed;
