import styled from 'styled-components';

export const AddAnnouncement = styled.h1`
    font-family: Monsterrat, sans-serif;
    text-align:center;
    background:#A3BDA7;
    border-radius:35px;
    margin:auto;
    padding:10px;
`

export const AddContact = styled.h1`
    font-family: Monsterrat, sans-serif;
`
export const DeleteContact = styled.h1`
    font-family: Monsterrat, sans-serif;
`

export const AddCommittee = styled.h1`
    font-family: Monsterrat, sans-serif;
`
export const Body = styled.div`
    display:flex;
    flex-direction:column;
    font-family: Monsterrat, sans-serif;

    margin:5px;
`
export const FormElements = styled.form`
    display:flex;
    flex-direction:column;
    justify-content:center;
    margin:auto;
    padding:5px;
    gap:5px;

`
